##setup
import random; r = random.randint
w1, w2 = words[r(0, len(words) - 1)], words[r(0, len(words) - 1)]
tries, c1, c2 = 13, '_____', '_____'
print("Welcome to my game of 'Wordle', except, you must guess two words at once!\nThe rules are simple, enter a 5 letter word and you will be shown if any of the letters belongs to either chosen word.\n\n")

##functions
def match(old, new):
  oldT = []
  for letter in old: oldT.append(letter) ## 
  for index in range(0,5):
    if old[index] != new[index] and old[index] == '_':
      oldT[index] = new[index]
  return ''.join(oldT)
  
def construct(word, check ,c):
  str = []
  for n in range(0,5):
    if word[n] == check[n]:
      str.append(word[n])
    else:
      str.append(c[n])
  return ''.join(str)
  
def getCorrect(w1,w2,c):
  correct, misplaced = [], []
  for n in range(0,5):
    if w1[n] == w2[n]:
      correct.append(w1[n])
    else:
      if (w1[n] in w2) and (w1[n] not in misplaced) and (w1[n] not in c):
        misplaced.append(w1[n])
  return [correct, misplaced]
  
def makePresentable(list):
  ss = ' '
  if len(list) > 1: ss = 's '
  return ss + (' and '.join(list))
while w2 == w1: w2 = words[r(0, len(words) - 1)]

##game
while tries > 0:
  print('~===================~ {} Tries Left ~===================~'.format(tries))
  i = input("\nGuess a 5 letter word: ").upper()
  if len(i) != 5: print("Please enter a 5 letter word, you have not lost a turn.\n"); continue
  tries -= 1
  
  c1, numc1 = match(c1, construct(i, w1, c1)), getCorrect(i,w1, c1)[1]
  c2, numc2 = match(c2, construct(i, w2, c2)), getCorrect(i,w2, c2)[1]

  if c1 == w1 and c2 == w2:
    print('It seems you have gotten both words! It was {} and {}!'.format(w1,w2)); break
  elif i == w1: print("Word 1: Completed! ({})\nWord 2 Completion:{}".format(w1, c2))
  elif i == w2: print("Word 1: Completion: {}\nWord 2: Completed! ({})".format(c1, w2))
  elif tries == 0 and c1 != w1 and c2 != w2: print('The words were {} and {}!'.format(w1,w2)); break
  else: print("Word 1 Completion: {}\nWord 2 Completion: {}".format(c1, c2))
  print('\n')

  if len(numc1) > 0: 
    print("The letter{} from the guessed word can be found in Word 1, just in another place".format(makePresentable(numc1)))
    
  if len(numc2) > 0: 
    print("The letter{} from the guessed word can be found in Word 2, just in another place.".format(makePresentable(numc2)))
  
  if len(numc1) == 0 and len(numc2) == 0:
    print("None of the letters are in a correct spot for either of the words.")

